 .ring-back {
	 opacity: 0.1;
	 filter: alpha(opacity=10);
	 width: 100%;
	 height: 100%;
	 border: solid 10px transparent;
	 border-radius: 50%;
}
 .ring-back:before {
	 position: absolute;
	 top: 5px;
	 right: 5px;
	 bottom: 5px;
	 left: 5px;
	 border-radius: 50%;
	 border: solid 35px #fff;
	 content: " ";
}
 .ring {
	 position: relative;
	 top: -100%;
	 width: 100%;
	 height: 100%;
	 -webkit-transition: all 0.5s;
	 -moz-transition: all 0.5s;
	 -ms-transition: all 0.5s;
	 -o-transition: all 0.5s;
	 transition: all 0.5s;
	 border: solid 45px #202020;
	 border-radius: 50%;
	 border-bottom-color: transparent;
	 box-shadow: 0px -2px 2px #000;
}
 .side-ring {
	 -webkit-transition: all 0.5s;
	 -moz-transition: all 0.5s;
	 -ms-transition: all 0.5s;
	 -o-transition: all 0.5s;
	 transition: all 0.5s;
	 width: 200px;
	 height: 200px;
	 background-color: #202020;
	 border-radius: 50%;
	 box-shadow: 0px 2px 2px #000;
	 color: #000;
	 overflow: hidden;
}
 #weather {
	 position: absolute;
	 top: calc(50% - 100px);
	 left: calc(20% - 100px);
}
 #steps {
	 position: absolute;
	 top: calc(50% - 100px);
	 left: calc(80% - 100px);
}
 .fa-cloud {
	 opacity: 0;
	 filter: alpha(opacity=0);
	 position: absolute;
	 top: calc(50% - 40px);
	 left: calc(50% - 40px);
	 color: #555;
	 font-size: 80px;
}
 .temperature {
	 opacity: 0;
	 filter: alpha(opacity=0);
	 position: absolute;
	 top: 10%;
	 left: 55%;
	 color: #fc0;
	 font-size: 20px;
}
 .bars {
	 opacity: 0;
	 filter: alpha(opacity=0);
	 position: relative;
	 top: calc(50% - 70px);
	 left: calc(50% - 65px);
	 width: 140px;
	 height: 140px;
}
 .bar {
	 width: 18px;
	 height: 140px;
	 margin: 0px -4px;
}
 .day-letter {
	 position: relative;
	 top: 110px;
	 color: #555;
	 font-size: 18px;
	 text-align: center;
}
 .x {
	 position: absolute;
	 bottom: 30px;
	 left: 1px;
	 width: 16px;
	 height: 2px;
	 -webkit-transition: all 0.5s;
	 -moz-transition: all 0.5s;
	 -ms-transition: all 0.5s;
	 -o-transition: all 0.5s;
	 transition: all 0.5s;
}
